package com.xcv58.leetcode.number_of_ways_to_split_a_string;

import static org.junit.Assert.*;

import org.junit.Test;

public class SolutionTest {

  @Test
  public void test() {
    Solution solution = new Solution();
    assertEquals(0, solution.numWays(null));
    assertEquals(0, solution.numWays(""));
    assertEquals(0, solution.numWays("00"));
    assertEquals(0, solution.numWays("1111"));
    assertEquals(0, solution.numWays("11111"));
    assertEquals(1, solution.numWays("111111"));
    assertEquals(1, solution.numWays("111111111"));
    assertEquals(4, solution.numWays("10101"));
    assertEquals(25, solution.numWays("10000100001"));
    assertEquals(5, solution.numWays("10000100001111"));
    assertEquals(1, solution.numWays("10000100001111111"));
    assertEquals(
      7367041,
      solution.numWays(
        ""
      )
    );
  }
}
